sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	// Add your code here
	today = GlideDateTime();
	request_time = GlideDateTime(current.u_when_needed);
	if(today.getDate() == request_time.getDate()) {
		gs.addErrorMessage("You must pick a date after the current date.");
		current.setAbortAction(true);
	}

})(current, previous);
